import React, { Component } from 'react';
import axios from 'axios';
import { BackButton, ImageCheckBox } from 'App/admin/helperComponents';
import PropTypes from 'prop-types';
import ImageErrorCatch from 'App/pages/ImageErrorCatch';
import { FaSortUp, FaSortDown, FaTrashAlt } from "react-icons/fa";  // Font Awesome
const layoutJson = require('App/layout.json');

export default class EditLayout extends Component{
	constructor(props){
		super(props)
		this.state ={
			menuStyle:layoutJson.navigationStyle,
			headerAlignment:layoutJson.headerAlignment,
			portfolioStyle:layoutJson.portfolioStyle,
			pages:[],
			deletedLinks:[],
			numberOfLinks:0
		}
		this.handleChange=this.handleChange.bind(this);
		this.createNewLink=this.createNewLink.bind(this);
		this.removeLink=this.removeLink.bind(this);
		this.changePageState=this.changePageState.bind(this);
		this.changeLinkOrder=this.changeLinkOrder.bind(this);
		// this.onSubmit=this.onSubmit.bind(this);
	}

	// need to load a sorted array of page links
	componentDidMount(){
		axios.get('/api/getPageInfo').then((response) => {
			console.log(response.data);
	     let orderedPages=response.data.sort(function(page1, page2) {
			  var key1 = (page1.index),
			    key2 = (page2.index);
			  // Compare the 2 dates
			  if (key1 < key2) return -1;
			  if (key1 > key2) return 1;
			  return 0;
			});
			this.setState({menuStyle:response.data.menuStyle, headerAlignment:response.data.headerAlignment, pages:orderedPages, numberOfLinks:orderedPages.length});
		})
	}

	handleChange = (event) => {
		this.setState({[event.target.name]:event.target.value})
	}

	createNewLink(){
		let values = [...this.state.pages];
		values.push({title: `Link ${this.state.numberOfLinks}`, type:"parent", visible:true, children:[], index:this.state.numberOfLinks});
		this.setState({pages:values, numberOfLinks:this.state.numberOfLinks+1});
	}

	removeLink(index){
		let values = [...this.state.pages];
		for (let i=index; i<values.length;i++){
			values[i].index=values[i].index-1;
		}
		values.splice(index, 1);
		this.setState({pages:values, numberOfLinks:this.state.numberOfLinks-1});
	}

	changePageState(index, key, value){
		let values = [...this.state.pages];
		values[index][key]=value;
		this.setState({pages:values});
	}


	changeLinkOrder(direction, index){
		var newPages = [...this.state.pages];
		if (direction==="up" && index>0){
			newPages[index].index = index-1;
			newPages[index-1].index = index;
		} else if (direction==="down" && index<this.state.pages.length-1){
			newPages[index].index = index+1;
			newPages[index+1].index = index;
		} 
		newPages.sort(function(page1, page2) {
		  var key1 = (page1.index),
		    key2 = (page2.index);
		  // Compare the 2 dates
		  if (key1 < key2) return -1;
		  if (key1 > key2) return 1;
		  return 0;
		});

		console.log(newPages);

		this.setState({pages:newPages});
	}

	render(){
		const createLinks = this.state.pages.map((page)=>{
			if (page.type==="parent"){
				return <li key={page.title}> <ParentLinkObject key={page.title} index={page.index} title={page.title} changeParentState={this.changePageState} changeOrder={this.changeLinkOrder} removeLink={this.removeLink}/></li>
			} else {
				return <li key={page.title}><LinkEditingObject key={page.title} index={page.index} title={page.title} changeParentState={this.changePageState} changeOrder={this.changeLinkOrder}/></li>
			}
		});

		return(
			<div className="pageEditor">
				<div className="inputGroup">
					<label className='inputLabel home' htmlFor='type'>Choose your navigation style:</label>
					<select name='menuStyle' className='navBarSelect' value={this.state.menuStyle} onChange={this.handleChange}>
						<option value='top'>Top Bar</option>
						<option value='dropdown'>Dropdown Menu</option>
						<option value='sidebar'>Sidebar Menu</option>
					</select>
				</div>
				<div className="inputGroup">
					<label className='inputLabel home' htmlFor='type'>Choose your website title orientation:</label>
					<select name='headerAlignment' className='navBarSelect' value={this.state.headerAlignment} onChange={this.handleChange}>
						<option value='left'>Left</option>
						<option value='center'>Center</option>
						<option value='right'>Right</option>
					</select>
				</div>
				<div className="linkEditing">
					<h3>You can edit the visibility and order of your menu links below</h3>
					{ createLinks }
				</div>


				<button type="button" onClick={this.createNewLink}>Add a Parent Link</button>
			</div>

		)
	}
}


// class for creating a parent link and giving it children. When children are added to its children list
// the respective children have their parent fields changed
class ParentLinkObject extends Component {
	constructor(props){
		super(props)
		this.state ={
			title:props.title,
			children:this.props.children
		}
		this.incrementLinkOrder=this.incrementLinkOrder.bind(this);
		this.decrementLinkOrder=this.decrementLinkOrder.bind(this);
		this.removeLink=this.removeLink.bind(this);
		this.handleChange=this.handleChange.bind(this);
	}

	handleChange = (event) => {
		if (event){
			this.setState({[event.target.name]:event.target.value})
			this.props.changeParentState(this.props.index, event.target.name, event.target.value);
		}
	}

	handleCheckClick=(event)=>{
		let values=[...this.state.children];
		if (event.target.checked){
			let index=this.state.children.indexOf(event.target.name);
			values.splice(index,1);
		} else {
			values.push(event.target.name);
		}

		this.setState({children:values});
		this.props.changeParentState(this.props.index, "children", values);
	}

	decrementLinkOrder(){
		this.props.changeOrder("up", this.props.index)
	}

	incrementLinkOrder(){
		this.props.changeOrder("down", this.props.index)
	}

	removeLink(){
		this.props.removeLink(this.props.index);
	}

	render(){
		const childrenOptions = () =>{
			this.props.pages.map((page)=>{
				if (page.type!=="parent"){
					return <input key={page._id} name={page._id} type="checkbox" checked={this.state.children.includes(page._id)} onChange={this.handleCheckClick} value={page.title}/>
				}
				return null;
			})
		}
		return(
			<div>
				<input type='text' className='smallPageField' name="title" value={this.state.title} onChange={this.handleChange}/>
				<div className="linkOrderArrows">
					<div className="upArrow" name="up" onClick={this.decrementLinkOrder}><FaSortUp/></div>
					<div className="downArrow" name="down" onClick={this.incrementLinkOrder}><FaSortDown/></div>
				</div>

				<button type="button" className="tooltip trashButton" 
					onClick={this.removeLink}>
			    	<FaTrashAlt />
				    <span className="tooltiptext">Remove this Link</span>
				</button>
			</div>
