// based on tutorial here: https://codeburst.io/image-uploading-using-react-and-node-to-get-the-images-up-c46ec11a7129

import React, { Component } from 'react';
import axios from 'axios';
import ImageUploader from "react-images-upload";
const FileReader = require('filereader');

export default class Contact extends Component {
	 imageArray = [];
   imageObj=[];
   imgageData=[];

  constructor(props) {
		super(props);
      this.state ={
        selectedFiles: []
      };
      this.uploadFiles = this.uploadFiles.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
   	}

    uploadFiles(e) {

      this.setState({
       selectedFiles: e.target.files,
      })

      this.imageObj.push(e.target.files);
      for (let i = 0; i < this.imageObj[0].length; i++) {
          this.imageArray.push(URL.createObjectURL(this.imageObj[0][i]))
      }
      this.setState({ file: this.imageArray });
      console.log(this.state);
    }

    getData(url, obj){
      console.log(this.imageData);
    }

    onSubmit(e) {

        const data = new FormData();
           for(var x = 0; x<this.state.selectedFiles.length; x++) {
               data.append('file', this.state.selectedFiles[x])
           }

          axios.post("/upload/uploadImages", data, { 
              // receive two    parameter endpoint url ,form data
          })

        .then(res => { // then print response status
            console.log(res.statusText)
         })

    }

  render(){

  	return(
  		<div className="main-upload-container"> 
  			<h3 className="main-heading"> Upload an Image </h3>
          <div className="form-group multi-preview">
              {(this.imageArray || []).map(url => (
                  <EditTag key={url} url={url} sendData={this.getData}/>
              ))}
          </div>
        <form>
          <div className="form-group">
              <input type="file" className="form-control" onChange={this.uploadFiles} multiple />
          </div>
          <button type="button" className="btn" onClick={this.onSubmit}>Upload</button>
        </form >
  		</div>
  	)
  }
 }


class EditTag extends Component {
  constructor(props){
    super(props);
    this.state = {
      url:this.props.url,
      title:'',
      date:'',
      medium:'',
      size:'',
      availability:'',
      price:'',
      portfolio:''
    }
    this.handlechange=this.handlechange.bind(this);
  }

  handleChange(e) {    
    this.setState({[e.target.name]: e.target.value});  
  }

  render(){

    return(
      <form className="editImageTag" onSubmit={this.props.sendData(this.props.url, this.state)} >
        <img src={this.props.url} className="uploadImage" alt="..."/>
        <input type="text" name="title" value={ this.state.title } placeholder="Title" onChange={this.handleChange} />
        <input type="text" name="date" value={ this.state.date } placeholder="Date" onChange={this.handleChange} />
        <input type="text" name="medium" value={ this.state.medium } placeholder="Medium" onChange={this.handleChange} />
        <input type="text" name="size" value={ this.state.size } placeholder="Size" onChange={this.handleChange} />
        <input type="text" name="price" value={ this.state.price } placeholder="Price" onChange={this.handleChange} />
        <select name="availability" value={this.state.availability} onChange={this.handleChange}>            
          <option value="forSale">For Sale</option>
          <option value="notForSale">Not For Sale</option>
          <option value="sold">Sold</option>
          <option value="other">Not Applicable</option>
        </select>
      </form>
      )
  }
 }
