// based on tutorial here: https://codeburst.io/image-uploading-using-react-and-node-to-get-the-images-up-c46ec11a7129

import React, { Component } from 'react';
import axios from 'axios';
import DefaultImg from '../profileimages/default-image.png'

const API_URL = "../../../../routes"

export default class Contact extends Component {
	constructor(props) {
		super(props);
		this.state = {
			multerImage : DefaultImg,
			firebaseImage: DefaultImg,
			baseImage: DefaultImg
		}
	}

	setDefaultImage(uploadType){
		if (uploadType === "multer") {
			this.setState({
				multerImage: DefaultImg
			});
		} else if (uploadType === "firebase") {
			this.setState({
				firebaseImage: DefaultImg
			});
		} else {
			this.setState({
				baseImage: DefaultImg
			});
		}
	}

	uploadAllImages(e, method){
		console.log(e);
		// e.target.files.map((file) => { this.uploadImage(file) });
	}

	uploadImage(file){
		let imageObj = {};

			let imageFormObj = new FormData();

			// TODO have url set properly
			imageFormObj.append("imageUrl", file);
			imageFormObj.append("imageName", "multer-image-"+ Date.now());
			imageFormObj.append("imageDate", "");
			imageFormObj.append("imageSize", "");
			imageFormObj.append("imageMedium", "");
			imageFormObj.append("imagePortfolio", "");

			this.setState({
				multerImage: URL.createObjectURL(file)
			});

			axios.post(`${API_URL}/image/uploadmulter`, imageFormObj)
			.then((data)=> {
				if (data.data.success) {
					alert("Image has been succesfully uploaded using multer");
					this.setDefaultImage("multer");
				}
			})
			.catch((err) => {
				alert("Error while uploading image");
				this.setDefaultImage("multer");
			}); 
	}




  render(){
  	return(

  		<div className="main-upload-container"> 
  			<h3 className="main-heading"> Upload Images </h3>
  			<div className ="image-container">
  				<form id="uploadForm" encType="multipart/form-data">
  					<p className="process_details">Upload images to your portfolio(s)</p>
  					<input type="file" className="process_upload-btn" onChange={(e) =>this.uploadAllImages(e)} multiple/>
  					<img src={this.state.multerImage} alt="upload-image" className="process_image" />
  				</form>
  			</div>
  		</div>



  		)
  }

 }
