// based on tutorial here: https://codeburst.io/image-uploading-using-react-and-node-to-get-the-images-up-c46ec11a7129

import React, { Component } from 'react';
import axios from 'axios';

export default class Contact extends Component {
	constructor(props) {
		super(props);

		this.uploadImages = this.uploadImages.bind(this);
	}



  handleSubmit(event) {
  	console.log(event);
    event.preventDefault();
    const data = new FormData(event.target);

    fetch('/api/form-submit-url', {
      method: 'POST',
      body: data,
    });
  }


	uploadImages(event, method){
		// [...e.target.files].map( file => {console.log(file)});
		event.preventDefault();
		const images = new FormData(event.target);

		axios.post('/image/uploadimages', images)
		.then((data)=> {
			if (data.data.success) {
				alert("Images have been succesfully uploaded using multer");
				this.setDefaultImage("multer");
			}
		})
		.catch((err) => {
			alert("Error while uploading these images");
			console.error(err);
			this.setDefaultImage("multer");
		});
	}


  render(){
  	return(

  		<div className="main-upload-container"> 
  			<h3 className="main-heading"> Upload Images </h3>
  			<div className ="image-container">
  				<form encType="multipart/form-data" onSubmit={this.uploadImages}>
  					<p className="process_details">Upload images to your portfolio(s)</p>
  					 <label htmlFor="uploadForm">Enter username</label>
  					<input id="uploadForm" name="uploadForm" type="file" className="process_upload-btn" multiple/>
  				    <button>Upload Images</button>
  				</form>
  			</div>
  		</div>



  		)
  }

 }
