// based on tutorial here: https://codeburst.io/image-uploading-using-react-and-node-to-get-the-images-up-c46ec11a7129

import React, { Component } from 'react';
import axios from 'axios';

export default class Contact extends Component {
	constructor(props) {
		super(props);
        this.state ={
            files: null
        };
   	}



	uploadImage(e){
      e.preventDefault();

      let imageFormObj = new FormData();
      imageFormObj.append("imageName", "multer-image-"+ Date.now());
      imageFormObj.append("imageData", e.target.files[0]);

      axios.post(`/image/uploadmulter`, imageFormObj)
        .then((data)=>{
          if(data.data.success){
            alert("Image succcesfully uploaded");
          }
        })
        .catch((err)=> {
          alert("Error while uploading image");
        });



    }

    onSubmit(e) {
      e.preventDefault();
      console.log(e);

    //   const options = {
    //     url: '/upload/multiple-upload',
    //     method: 'POST',
    //     headers: {
    //       'Accept': 'application/json',
    //       'Content-Type': 'application/json;charset=UTF-8'
    //     },
    //     data: styleObject
    //   };

    // axios(options)
    //   .then(response => {
    //     console.log(response.status);
    //   });

    }


  render(){
  	return(

  		<div className="main-upload-container"> 
  			<h3 className="main-heading"> Upload an Image </h3>
  			<div className ="image-container">

  				<form encType="multipart/form-data">
  					<p className="process_details">Upload images to your portfolio(s)</p>
  					<input name="uploadForm" type="file" onChange= {(e)=>this.uploadImage(e)}/>
  				    <button type="submit">Upload Images</button>
  				</form>


          <form encType="multipart/form-data" onSubmit={this.onSubmit}>
            <div className="form-group">
              <label htmlFor="example-input-file"> Upload multiple images </label>
              <input type="file" name="multi-files" multiple id="input-multi-files" className="form-control-file border"/>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
          <div className="preview-images"></div>
  			</div>
  		</div>



  		)
  }

 }
