// based on tutorial here: https://codeburst.io/image-uploading-using-react-and-node-to-get-the-images-up-c46ec11a7129

import React, { Component } from 'react';
import axios from 'axios';
import ImageUploader from "react-images-upload";

export default class Contact extends Component {
	constructor(props) {
		super(props);
        this.fileInput = React.createRef();
        this.handleSubmit = this.handleSubmit.bind(this);

        this.onDrop = this.onDrop.bind(this);

        this.state ={
            files: null,
            pictures: []
        };
   	}

  onDrop(picture) {
      this.setState({
          pictures: this.state.pictures.concat(picture),
      });
      console.log(this.state.pictures);
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(
      this.fileInput.current.files
    );

    // let imageFormObj = new FormData();
    // imageFormObj.append("name", "multi-files");
    // imageFormObj.append("files", this.fileInput.current.files);

    axios.post('/upload/multiple-upload', this.fileInput.current.files)
      .then((data)=>{
        if(data.data.success){
          alert("Image succcesfully uploaded");
        }
      })
      .catch((err)=> {
        alert("Error while uploading image");
      });

  }

	uploadImage(e){
      e.preventDefault();

      let imageFormObj = new FormData();
      imageFormObj.append("imageName", "multer-image-"+ Date.now());
      imageFormObj.append("imageData", e.target.files[0]);

      axios.post(`/image/uploadmulter`, imageFormObj)
        .then((data)=>{
          if(data.data.success){
            alert("Image succcesfully uploaded");
          }
        })
        .catch((err)=> {
          alert("Error while uploading image");
        });



    }

    onChange(e){
      console.log(e.target);
    }

