    //     headers: {
    //       'Accept': 'application/json',
    //       'Content-Type': 'application/json;charset=UTF-8'
    //     },
    //     data: styleObject
    //   };

    // axios(options)
    //   .then(response => {
    //     console.log(response.status);
    //   });

    }


  render(){
  	return(

  		<div className="main-upload-container"> 
  			<h3 className="main-heading"> Upload an Image </h3>
        <ImageUploader
              withIcon={false}
              withPreview={true}
              label=""
              buttonText="Upload Images"
              onChange={this.onDrop}
              imgExtension={[".jpg", ".jpeg", ".gif", ".png", ".gif", ".svg"]}
              maxFileSize={1048576}
              fileSizeError=" file size is too big"
          />
          <form encType="multipart/form-data" onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="example-input-file"> Upload multiple images </label>
              <input type="file" name="multi-files" ref={this.fileInput} multiple />

            </div>
            <button type="submit">Submit</button>
          </form>
          <div className="preview-images"></div>
  		</div>




  		)
  }

 }
