    //     data: styleObject
    //   };

    // axios(options)
    //   .then(response => {
    //     console.log(response.status);
    //   });

    }


  render(){
  	return(

  		<div className="main-upload-container"> 
  			<h3 className="main-heading"> Upload an Image </h3>
  			<div className ="image-container">

  				<form encType="multipart/form-data">
  					<p className="process_details">Upload images to your portfolio(s)</p>
  					<input name="uploadForm" type="file" onChange= {(e)=>this.uploadImage(e)}/>
  				    <button type="submit">Upload Images</button>
  				</form>


          <form encType="multipart/form-data" onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="example-input-file"> Upload multiple images </label>
              <input type="file" name="multi-files" ref={this.fileInput} multiple />

            </div>
            <button type="submit">Submit</button>
          </form>
          <div className="preview-images"></div>
  			</div>
  		</div>




  		)
  }

 }
