import React, { Component } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

import { authenticationService } from 'App/admin/authentication/authenticationService'

export default class EditProfile extends Component {
	constructor(props) {
        super(props);
        this.state={
            name:'',
            email:'',
            pswd:'',
            confirm:''
        }

        this.onSubmit=this.onSubmit.bind(this);
	}

    componentDidMount(){
        axios.get('/user/getUser').then(response=>{
            this.setState({name:response.data.name, email:response.data.email});
        });
    }

    onSubmit(setStatus){
        const user = this.state;
        setStatus();

        if (user.name && user.email && user.pswd && user.confirm && user.pswd === user.confirm){
            axios.post('/user/editUser', user, {}).then(res=> { // then print response status
                alert(res.statusText);
            }).catch(err => console.log(err)); 
        } else if (user.pswd !== user.confirm){
            alert('New Passwords must match!')
        } else {
            alert('Please check that all fields are entered')
        }
    }

	render() {
        return (
            <div>
                <h2>Create Your Profile</h2>
                <Formik
                    initialValues={{
                        username: this.state.name,
                        email: this.state.email,
                        oldPassword:'',
                        password: '',
                        confirm: ''
                    }}
                    validationSchema={Yup.object().shape({
                        username: Yup.string().required('Username is required'),
                        email: Yup.string().email().required('A valid email is required'),
                        oldPassword: Yup.string().required('Your old password is required'),
                        password: Yup.string().required('Password is required'),
                        confirm: Yup.string().required('Please confirm your password')

                    })}
                    onSubmit={({username, email, password, confirm}, { setStatus }) => {        
                        if (username && email && password && confirm && password === confirm){
                            axios.post('/user/editUser', {name:username, email:email, password:password, confirm:confirm}, {}).then(res=> { // then print response status
                                alert(res.statusText);
                            }).catch(err => console.log(err)); 
                        } else if (password !== confirm){
                            alert('Passwords must match!')
                        } else {
                            alert('Please check that all fields are entered correctly')
                        }}
                    }
                >
                    {({ errors, status, touched, isSubmitting }) => (
                            <Form>
                                <div className="form-group">
                                    <label htmlFor="username">Your Name</label>
                                    <Field name="username" type="text" className={'form-control' + (errors.username && touched.username ? ' is-invalid' : '')} />
                                    <ErrorMessage name="username" component="div" className="invalid-feedback" />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="email">Email</label>
                                    <Field name="email" type="text" className={'form-control' + (errors.email && touched.email ? ' is-invalid' : '')} />
                                    <ErrorMessage name="emaile" component="div" className="invalid-feedback" />
                                </div>                            
                                <div className="form-group">
                                    <label htmlFor="oldPassword">Old Password</label>
                                    <Field name="oldPassword" type="text" className={'form-control' + (errors.oldPassword && touched.oldPassword ? ' is-invalid' : '')} />
                                    <ErrorMessage name="oldPassword" component="div" className="invalid-feedback" />
                                </div>    
                                <div className="form-group">
                                    <label htmlFor="password">New Password</label>
                                    <Field name="password" type="password" className={'form-control' + (errors.password && touched.password ? ' is-invalid' : '')} />
                                    <ErrorMessage name="password" component="div" className="invalid-feedback" />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="confirm">Confirm Password</label>
                                    <Field name="confirm" type="text" className={'form-control' + (errors.confirm && touched.confirm ? ' is-invalid' : '')} />
                                    <ErrorMessage name="confirm" component="div" className="invalid-feedback" />
                                </div>                            <div className="form-group">
                                    <button type="submit" className="btn btn-primary" disabled={isSubmitting}>Register</button>
                                    {isSubmitting &&
                                        <img src="data:image/gif;base64," />
                                    }
                                </div>
                                {status &&
                                    <div className={'alert alert-danger'}>{status}</div>
                                }
                            </Form>
                        )}
                    </Formik>
            </div>
        )
    }
}
