import React, { Component, useState } from 'react';
import { SketchPicker } from 'react-color'
const styleJson = require('../style.json');
const newJson = JSON.parse(JSON.stringify(styleJson));

export default class Contact extends Component {
	constructor(props) {
		super(props);
		this.onColorChangeComplete = this.onColorChangeComplete.bind(this);
		this.onTextChange = this.onTextChange.bind(this);
	}

	state = {
		selectedColor: '#fff',
		styleObject: newJson,
	}

	onColorChangeComplete = (color) => {
		this.setState({ selectedColor: color.hex });
	}

	oChange = (e)  => {
    	this.setState({ [e.target.id]: e.target.value });
	};



	onSubmit =(event) =>{
		event.preventDefault();
		fetch('/style/changeStyle',{
			method:'POST',
			body:JSON.stringify(this.state.styleObject),
			headers:{
				'Content-Type': 'application/json'
			}
		})
		.then(res => {
			if(res.status===200){
				this.props.history.push('/');
			} else {
				const error = new Error(res.error);
				throw error;
			}
		})
		.catch(err=>{
			console.error(err);
			alert('Error changing website styling, please try again');
		});
	}

	onCancel = (event) =>{
		alert("Style values were not saved");
		const newJson = JSON.parse(JSON.stringify(styleJson));
		this.setState({
			styleObject:newJson
		})
	}

	render(){
		return(
			<div className="Editor">
				<form className="EditorForm">
					<TextEditorFields textData={this.state.styleObject.text}/>
					<button type="submit" value="Update">Update</button>
					<button type="submit" value="Save" onClick={this.onSubmit}>Save</button>
					<button type="reset" value="Cancel" onClick={this.onCancel}>Cancel</button>
				</form>
			</div>
		)
	}

}       


function TextStyle(props){
	const [fontSize, setFontSize] = useState(props.fontSize);
	const handleChange = e => {
	    setFontSize(e.target.value);
	};

	return(
		<div>
			<h2> {props.name} </h2>
			<label htmlFor={'color-'+ props.name }> Change color</label>
			<input type="text" name={'color-'+ props.name } value={props.color}/>
			<label htmlFor={'size-'+ props.name }> Change font size</label>
			<input type="range" name={'size-'+ props.name } min="10" max="44" onMouseUp={handleChange} className="sizeSlider"/>
		</div>
	)
}

function TextEditorFields(props){
	// console.log(props);
	const textStyleFields = Object.entries(props.textData).map(([key, value]) => 
	    <TextStyle name={key} color={value.color} fontSize={value.size}/>
	);
	return textStyleFields;
}



function ColorSelector(props) {

	return (
      <SketchPicker
        color={ props.initColor }
        onChangeComplete={ this.onColorChangeComplete }
     />

	)
}
